INSERT INTO `course` VALUES (1,'Cách học cờ vua','Khóa học này dành cho người mới chơi. Người học sẽ học được các thế cờ cơ bản, cách phân biệt quân cờ. Qua khóa học này, người dùng có thể dễ dàng học tiếp các khóa học sau ở mức độ cao hơn..','2019-05-25 16:02:08',1,2,'https://i.ytimg.com/vi/6K_MElXH694/maxresdefault.jpg',52,'2019-08-19 17:43:47'),(870,'Cơ bản','Thịch hợp với tất cả đối tượng','2019-07-29 10:47:07',4,2,'https://firebasestorage.googleapis.com/v0/b/cols-fpt.appspot.com/o/images%2Fcourses%2Ftungltse61895%2FC%C6%A1%20b%E1%BA%A3n?alt=media&token=4f81b19e-6c16-4466-a9ba-862e87a6e5bd',207,'2019-08-25 23:31:40'),(908,'Nhập môn','Khóa học này dành cho người mới chơi. Người học sẽ học được các thế cờ cơ bản, cách phân biệt quân cờ. Qua khóa học này, người dùng có thể dễ dàng học tiếp các khóa học sau ở mức độ cao hơn..','2019-07-30 15:58:00',3,2,'https://firebasestorage.googleapis.com/v0/b/cols-fpt.appspot.com/o/images%2Fcourses%2Ftuyetnganphamhoang%2Fnh%E1%BA%ADp-m%C3%B4n?alt=media&token=c4cef16e-19ce-48db-8702-4d8f4f8412e7',276,'2019-08-24 10:38:12'),(925,'Tổng Quan Về Cờ Vua','Khóa học giúp cho những người chưa biết chơi cờ vua, hiểu được cách di chuyển của quân cờ, cách nhập thành.','2019-07-31 08:10:48',5,2,'https://firebasestorage.googleapis.com/v0/b/cols-fpt.appspot.com/o/images%2Fcourses%2Ftungltse61895%2Ft%E1%BB%95ng-quan-v%E1%BB%81-c%E1%BB%9D-vua?alt=media&token=cfea552f-b12d-4593-97d2-52ae897a346e',207,NULL),(1927,'Khóa học của Hòa','Khóa học này không cần biết gì cũng học được','2019-08-16 15:15:42',2,1,'https://firebasestorage.googleapis.com/v0/b/cols-fpt.appspot.com/o/images%2Fcourses%2Fldh24397123%2Fkh%C3%B3a-h%E1%BB%8Dc-c%E1%BB%A7a-h%C3%B2a?alt=media&token=89152862-f295-4106-b9f4-35944bb8c920',1868,'2019-08-16 15:21:24'),(1986,'Khóa học về các quân cờ','Trong bài học này, chúng ta sẽ học cách chơi cờ vua cơ bản, cách di chuyển của các quân cờ và cách ăn quân đối phương như thế nào\n\nỞ bài học trước, chúng ta đã học về khái niệm cờ vua, bàn cờ, quân cờ và cách xếp quân cờ. Bài học dưới đây sẽ hướng dẫn các bạn cách chơi cờ vua cơ bản bằng hình ảnh','2019-08-21 06:44:17',2,1,'https://firebasestorage.googleapis.com/v0/b/cols-fpt.appspot.com/o/images%2Fcourses%2Fhoangdvse62687%2Fkh%C3%B3a-h%E1%BB%8Dc-v%E1%BB%81-c%C3%A1c-qu%C3%A2n-c%E1%BB%9D?alt=media&token=f28bd23e-c8ad-467e-b505-3f9bda5249e4',52,NULL),(2231,'Cờ vua căn bản','Khóa học này giới thiệu những đòn tấn công cơ bản trong cờ vua.','2019-08-24 08:51:46',4,2,'https://firebasestorage.googleapis.com/v0/b/cols-fpt.appspot.com/o/images%2Fcourses%2Ftuyetnganphamhoang%2Fkhoa-hoc-co-ban?alt=media&token=e7f85585-eedc-43d5-ab29-b96a5cfd864f',276,'2019-08-25 23:27:21'),(2468,'Khóa học cờ vua mới nhất năm 2019','Khóa học này dạy bạn đánh cờ vua từ sơ cấp đến chuyên nghiệp','2019-08-25 23:29:37',3,2,'https://firebasestorage.googleapis.com/v0/b/cols-fpt.appspot.com/o/images%2Fcourses%2Finstructor.cols%2Fkh%C3%B3a-h%E1%BB%8Dc-c%E1%BB%9D-vua-m%E1%BB%9Bi-nh%E1%BA%A5t-n%C4%83m-2019?alt=media&token=8ac22092-f8f8-4b51-aeea-83279268a536',2262,'2019-08-25 23:29:59'),(2472,'Giới thiệu về cờ vua','Cờ vua (tên tiếng Anh: Chess), trước kia còn được gọi là Cờ quốc tế, là trò chơi quốc tế và là môn thể thao trí tuệ cho 2 người chơi.','2019-08-26 00:47:31',2,5,'https://firebasestorage.googleapis.com/v0/b/cols-fpt.appspot.com/o/images%2Fcourses%2Ftuyetnganphamhoang%2Fnh%E1%BA%ADp-m%C3%B4n?alt=media&token=c4cef16e-19ce-48db-8702-4d8f4f8412e7',276,'2019-09-18 12:13:39'),(2473,'Khóa học bắt thế cờ','Khoá học này sẽ giúp học viên bắt được thế cờ của đối phương ','2019-08-26 00:47:59',5,4,'https://firebasestorage.googleapis.com/v0/b/cols-fpt.appspot.com/o/images%2Fcourses%2Finstructor.cols%2Fkh%C3%B3a-h%E1%BB%8Dc-b%E1%BA%AFt-th%E1%BA%BF-c%E1%BB%9D?alt=media&token=d26c0e23-74ee-44c7-aa43-f71f56d2c062',2262,'2019-08-26 00:51:43'),(2498,'Khóa học cơ bản về cờ vua','Cờ Vua là một trò chơi có nguồn gốc từ Ấn Độ. Vào thế kỷ thứ 6 sau công nguyên. Cuộc chinh phục của người Ba Tư đã đưa Cờ Vua đến các nước Tây Á rồi sang Châu Âu.','2019-08-26 01:46:37',2,2,'https://firebasestorage.googleapis.com/v0/b/cols-fpt.appspot.com/o/images%2Fcourses%2Finstructor.cols%2Fkh%C3%B3a-h%E1%BB%8Dc-c%C6%A1-b%E1%BA%A3n-v%E1%BB%81-c%E1%BB%9D-vua?alt=media&token=bf33aed9-7e1f-4f9d-a373-6c569c56888c',2262,'2019-08-26 01:56:27'),(2785,'Chiếu bí trong Cờ Vua','Hướng dẫn chiếu Bí trong Cờ Vua','2019-09-25 08:12:53',1,2,'https://firebasestorage.googleapis.com/v0/b/cols-fpt.appspot.com/o/images%2Fcourses%2Ftuyetnganphamhoang%2Fchi%E1%BA%BFu-b%C3%AD-trong-c%E1%BB%9D-vua?alt=media&token=64426e89-cd0d-4e7f-9244-432688210c9e',276,'2019-09-25 08:37:55'),(2973,'Khóa học 2019 - Phần 1','Khóa học 2019 - Phần 1 edit 1','2019-09-27 12:16:03',2,2,'https://firebasestorage.googleapis.com/v0/b/cols-fpt.appspot.com/o/images%2Fcourses%2Finstructor.cols%2Fkh%C3%B3a-h%E1%BB%8Dc-2019---ph%E1%BA%A7n-1?alt=media&token=33bde727-9470-418c-ab0f-316a82fb869d',2262,'2019-09-27 12:25:39'),(2976,'Khóa học 2019 - Phần 2','Khóa học 2019 - Phần 2','2019-09-27 12:26:50',2,2,'https://firebasestorage.googleapis.com/v0/b/cols-fpt.appspot.com/o/images%2Fcourses%2Finstructor.cols%2Fkh%C3%B3a-h%E1%BB%8Dc-2019---ph%E1%BA%A7n-2?alt=media&token=fd0aa39f-fe91-4644-b398-1f786c32ab53',2262,'2019-09-27 12:27:59'),(2978,'Khóa học 2019 - Phần 3','Khóa học 2019 - Phần 3','2019-09-27 12:27:23',2,2,'https://firebasestorage.googleapis.com/v0/b/cols-fpt.appspot.com/o/images%2Fcourses%2Finstructor.cols%2Fkh%C3%B3a-h%E1%BB%8Dc-2019---ph%E1%BA%A7n-3?alt=media&token=01ae4561-30c0-4699-85a0-1ac04bc13549',2262,'2019-09-27 12:28:35');
